package com.geeks.tilek_talaibekov_hw_3_8

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.core.os.bundleOf
import androidx.navigation.fragment.findNavController
import com.geeks.tilek_talaibekov_hw_3_8.databinding.FragmentMainBinding

class MainFragment : Fragment() {

    private lateinit var binding: FragmentMainBinding

    private val seriesList = arrayListOf(
        Series("data:image/jpeg;base64,", "Alive", "Rick Sanchez"),
        Series("data:image/jpeg;base64,", "Alive", "Morty Smith"),
        Series("data:image/jpeg;base64,", "Alive", "Albert Einstein"),
        Series("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQDXgvahhC7zvZmAJF5fciKcGh_iaKhzisunA&usqp=CAU", "Alive", "Jerry Smith")
    )

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentMainBinding.inflate(layoutInflater)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        initAdapter()
    }

    private fun initAdapter() {
        val adapter = SeriesAdapter(seriesList, this::onClick)
        binding.rvPoster.adapter = adapter
    }

    private fun onClick(model: Series) {
        findNavController().navigate(R.id.nextFragment, bundleOf("start" to model))
    }

}